import React, { useState, useEffect, useMemo } from 'react';
import axios from 'axios';
import './VehiclesPage.css';

function VehiclesPage() {
  const [vehicles, setVehicles] = useState([]);
  const [isEditing, setIsEditing] = useState(false);
  const [currentVehicle, setCurrentVehicle] = useState(null);
  const [filter, setFilter] = useState('');
  const [photoFiles, setPhotoFiles] = useState([]);

  const initialFormData = {
    brand: '', model: '', year: '', color: '', gear: '', fuel: '',
    mileage: '', purchase_price: '', sale_price: '', description: ''
  };
  const [formData, setFormData] = useState(initialFormData);

  // Token'ı ve yetkilendirme başlığını en başta hazırlayalım
  const token = localStorage.getItem('token');
  const authHeaders = { headers: { 'Authorization': `Bearer ${token}` } };

  const fetchVehicles = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/vehicles');
      setVehicles(response.data);
    } catch (error) { console.error("Araçlar çekilirken hata oluştu:", error); }
  };

  useEffect(() => { fetchVehicles(); }, []);

  const handleInputChange = (e) => {
    setFormData(prev => ({ ...prev, [e.target.name]: e.target.value }));
  };

  const handleFileChange = (e) => {
    setPhotoFiles(Array.from(e.target.files));
  };

  const cancelEdit = () => {
    setIsEditing(false);
    setCurrentVehicle(null);
    setFormData(initialFormData);
    setPhotoFiles([]);
  };

  const handleEdit = async (vehicle) => {
    try {
      const response = await axios.get(`http://localhost:5000/api/vehicles/${vehicle.id}`);
      setIsEditing(true);
      setCurrentVehicle(response.data);
      setFormData(response.data);
      setPhotoFiles([]);
      window.scrollTo(0, 0);
    } catch (error) { alert("Araç detayları çekilirken bir hata oluştu."); }
  };
  
  const handleDeletePhoto = async (photoId) => {
    if (window.confirm("Bu fotoğrafı kalıcı olarak silmek istediğinizden emin misiniz?")) {
      try {
        await axios.delete(`http://localhost:5000/api/photos/${photoId}`, authHeaders);
        alert("Fotoğraf silindi.");
        setCurrentVehicle(prev => ({
            ...prev,
            photos: prev.photos.filter(p => p.id !== photoId)
        }));
      } catch (error) { alert("Fotoğraf silinirken bir hata oluştu."); }
    }
  };

  const handleDelete = async (id) => {
    if (window.confirm("Bu aracı ve TÜM fotoğraflarını silmek istediğinizden emin misiniz?")) {
      try {
        await axios.delete(`http://localhost:5000/api/vehicles/${id}`, authHeaders);
        alert("Araç başarıyla silindi.");
        fetchVehicles();
        if (isEditing) cancelEdit(); // Eğer düzenlenen araç silindiyse formu temizle
      } catch (error) { alert("Araç silinirken bir hata oluştu."); }
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const config = { headers: { 'Content-Type': 'multipart/form-data', 'Authorization': `Bearer ${token}` } };
    
    try {
      if (isEditing) {
        // 1. Metin verilerini güncelle
        await axios.put(`http://localhost:5000/api/vehicles/${currentVehicle.id}`, formData, authHeaders);

        // 2. Eğer yeni fotoğraf seçilmişse, onları ekle
        if (photoFiles.length > 0) {
          const photoData = new FormData();
          photoFiles.forEach(file => { photoData.append('photos', file); });
          await axios.post(`http://localhost:5000/api/vehicles/${currentVehicle.id}/add-photos`, photoData, config);
        }
        alert('Araç başarıyla güncellendi!');
      } else {
        // Yeni araç ekleme
        const vehicleData = new FormData();
        for (const key in formData) { vehicleData.append(key, formData[key]); }
        photoFiles.forEach(file => { vehicleData.append('photos', file); });
        await axios.post('http://localhost:5000/api/vehicles', vehicleData, config);
        alert('Araç başarıyla eklendi!');
      }
      cancelEdit();
      fetchVehicles();
    } catch (error) {
      console.error("İşlem hatası:", error);
      alert('İşlem sırasında bir hata oluştu.');
    }
  };

  const filteredVehicles = useMemo(() => {
    const searchTerm = filter.toLowerCase();
    if (!searchTerm) return vehicles;
    return vehicles.filter(v => Object.values(v).some(val => String(val).toLowerCase().includes(searchTerm)));
  }, [vehicles, filter]);

  return (
    <div className="vehicle-page-wrapper">
      <div className="vehicle-page">
        <div className="form-container">
          <h2 className="page-title">{isEditing ? 'Aracı Düzenle' : 'Yeni Araç Kayıt'}</h2>
          <form className="vehicle-form" onSubmit={handleSubmit}>
            <div className="form-row">
              <input type="text" name="brand" placeholder="Marka" value={formData.brand} onChange={handleInputChange} required />
              <input type="text" name="model" placeholder="Model" value={formData.model} onChange={handleInputChange} required />
              <input type="number" name="year" placeholder="Yıl" value={formData.year} onChange={handleInputChange} required />
            </div>
            <div className="form-row">
              <input type="text" name="color" placeholder="Renk" value={formData.color} onChange={handleInputChange} required />
              <input type="text" name="gear" placeholder="Vites" value={formData.gear} onChange={handleInputChange} required />
              <input type="text" name="fuel" placeholder="Yakıt" value={formData.fuel} onChange={handleInputChange} required />
            </div>
            <div className="form-row">
              <input type="number" name="mileage" placeholder="Kilometre" value={formData.mileage} onChange={handleInputChange} required />
              <input type="number" name="purchase_price" placeholder="Alış Fiyatı" value={formData.purchase_price} onChange={handleInputChange} required />
              <input type="number" name="sale_price" placeholder="Satış Fiyatı" value={formData.sale_price} onChange={handleInputChange} required />
            </div>
            <div className="form-row">
              <textarea name="description" placeholder="Açıklama" value={formData.description} onChange={handleInputChange}></textarea>
            </div>
            
            <div className="form-row">
              <div>
                <label htmlFor="photo-upload" className="photo-upload-label">
                  {isEditing ? 'Yeni Fotoğraflar Ekle' : 'Araç Fotoğrafları (En fazla 10 adet)'}
                </label>
                <input id="photo-upload" type="file" name="photos" onChange={handleFileChange} multiple accept="image/*" />
              </div>
            </div>

            {isEditing && currentVehicle?.photos?.length > 0 && (
              <div className="photo-management-container">
                  <h3>Mevcut Fotoğraflar</h3>
                  <div className="photo-grid">
                      {currentVehicle.photos.map(photo => (
                          <div key={photo.id} className="photo-item">
                              <img src={`http://localhost:5000/${photo.photo_url}`} alt="Araç"/>
                              <button type="button" onClick={() => handleDeletePhoto(photo.id)} className="delete-photo-btn">X</button>
                          </div>
                      ))}
                  </div>
              </div>
            )}
            
            <div className="form-buttons-container">
              <button type="submit" className="add-vehicle-btn">
                {isEditing ? 'Değişiklikleri Kaydet' : 'Aracı Ekle'}
              </button>
              {isEditing && <button type="button" className="add-vehicle-btn cancel-edit-btn" onClick={cancelEdit}>İptal</button>}
            </div>
          </form>
        </div>
        
        <div className="table-card">
          <h2>Kayıtlı Araçlar</h2>
          <div className="filter-container">
            <input type="text" placeholder="Araç listesinde ara..." value={filter} onChange={(e) => setFilter(e.target.value)} />
          </div>
          <div className="table-wrapper">
            <table>
              <thead>
                <tr>
                  <th>Fotoğraf</th><th>Marka</th><th>Model</th><th>Yıl</th><th>Fiyat</th>
                  <th className="sticky-col">İşlemler</th>
                </tr>
              </thead>
              <tbody>
                {filteredVehicles.map((vehicle) => (
                  <tr key={vehicle.id}>
                    <td>
                      <img src={`http://localhost:5000/${vehicle.photo_url}`} alt="Ana Foto" className="table-photo" onError={(e) => { e.target.style.display='none'; }}/>
                    </td>
                    <td>{vehicle.brand}</td>
                    <td>{vehicle.model}</td>
                    <td>{vehicle.year}</td>
                    <td className="price-cell">{parseFloat(vehicle.sale_price).toLocaleString('tr-TR', { style: 'currency', currency: 'TRY' })}</td>
                    <td className="sticky-col">
                      <div className="action-buttons">
                        <button className="edit-btn" onClick={() => handleEdit(vehicle)}>Düzenle</button>
                        <button className="delete-btn" onClick={() => handleDelete(vehicle.id)}>Sil</button>
                      </div>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      </div>
    </div>
  );
}

export default VehiclesPage;
