import React, { useState } from 'react';
import './MarketValuePage.css'; // Özel CSS dosyanızın yolu doğruysa bu satırı koruyun.

function MarketValuePage() {
    // Tüm filtreler için bir state oluşturuyoruz
    const [filters, setFilters] = useState({
        brand: '',
        model: '',
        yearMin: '',
        yearMax: '',
        kmMax: '',
        fuelTypes: [],
        gearTypes: [],
    });

    // Form inputlarını güncelleyen fonksiyon
    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setFilters(prev => ({ ...prev, [name]: value }));
    };

    // Checkbox değişikliklerini yöneten fonksiyon
    const handleCheckboxChange = (e) => {
        const { name, value, checked } = e.target;
        setFilters(prev => {
            const currentValues = prev[name]; // örn: filters.fuelTypes
            if (checked) {
                return { ...prev, [name]: [...currentValues, value] }; // Yeni değeri ekle
            } else {
                return { ...prev, [name]: currentValues.filter(item => item !== value) }; // Değeri çıkar
            }
        });
    };

    // Sahibinden.com için doğru URL'i oluşturan ve yönlendiren fonksiyon
    const redirectToSahibinden = () => {
        // Temel URL her zaman bu olmalı
        const baseUrl = 'https://www.sahibinden.com/otomobil';
        const params = new URLSearchParams();

        // ANAHTAR DEĞİŞİKLİK: Marka ve Modeli birleştirerek tek bir arama parametresi yapıyoruz.
        // Bu, sahibinden'in genel arama çubuğunu kullanır ve en güvenilir yöntemdir.
        const searchText = `${filters.brand} ${filters.model}`.trim();
        if (searchText) {
            // 'query_text_mf' parametresi marka/model araması için kullanılır.
            params.append('query_text_mf', searchText);
        }

        // Diğer parametreler doğru şekilde ekleniyor
        if (filters.yearMin) params.append('a5_min', filters.yearMin);
        if (filters.yearMax) params.append('a5_max', filters.yearMax);
        if (filters.kmMax) params.append('a4_max', filters.kmMax);
        
        // Checkbox'lar için her bir seçili değeri ayrı ayrı ekliyoruz
        filters.fuelTypes.forEach(fuel => params.append('a3', fuel)); // a3 = Yakıt
        filters.gearTypes.forEach(gear => params.append('a2', gear)); // a2 = Vites

        // Parametreleri URL'e ekleyerek nihai adresi oluşturuyoruz.
        const finalUrl = `${baseUrl}?${params.toString()}`;
        
        console.log("Oluşturulan Doğru URL:", finalUrl);

        // Kullanıcıyı yeni bir sekmede bu URL'e yönlendir
        window.open(finalUrl, '_blank');
    };

    return (
        <div className="market-page-wrapper">
            <div className="market-page-container">
                <div className="card">
                    <h3>Piyasa Analizi için Araç Filtrele</h3>
                    <p className="description">
                        Aşağıdaki formu doldurarak, seçtiğiniz aracın piyasa durumunu popüler satış platformlarında anında görün.
                    </p>

                    <div className="filter-grid">
                        {/* === METİN GİRİŞLERİ === */}
                        <div className="form-group">
                            <label>Marka</label>
                            <input name="brand" value={filters.brand} onChange={handleInputChange} placeholder="örn: Volkswagen" />
                        </div>
                        <div className="form-group">
                            <label>Model</label>
                            <input name="model" value={filters.model} onChange={handleInputChange} placeholder="örn: Passat" />
                        </div>
                        <div className="form-group">
                            <label>Yıl (En Az)</label>
                            <input name="yearMin" type="number" value={filters.yearMin} onChange={handleInputChange} placeholder="örn: 2020" />
                        </div>
                        <div className="form-group">
                            <label>Yıl (En Çok)</label>
                            <input name="yearMax" type="number" value={filters.yearMax} onChange={handleInputChange} placeholder="örn: 2022" />
                        </div>
                        <div className="form-group">
                            <label>Kilometre (En Çok)</label>
                            <input name="kmMax" type="number" value={filters.kmMax} onChange={handleInputChange} placeholder="örn: 100000" />
                        </div>

                        {/* === CHECKBOX GRUPLARI === */}
                        <div className="checkbox-group">
                            <label>Yakıt Tipi</label>
                            <div>
                                {/* Sahibinden.com'daki gerçek 'value' değerlerini kullanıyoruz */}
                                <label><input type="checkbox" name="fuelTypes" value="1" onChange={handleCheckboxChange} /> Benzin</label>
                                <label><input type="checkbox" name="fuelTypes" value="2" onChange={handleCheckboxChange} /> Dizel</label>
                                <label><input type="checkbox" name="fuelTypes" value="3" onChange={handleCheckboxChange} /> LPG</label>
                            </div>
                        </div>
                        <div className="checkbox-group">
                            <label>Vites Tipi</label>
                            <div>
                                <label><input type="checkbox" name="gearTypes" value="1" onChange={handleCheckboxChange} /> Manuel</label>
                                <label><input type="checkbox" name="gearTypes" value="2" onChange={handleCheckboxChange} /> Otomatik</label>
                            </div>
                        </div>
                    </div>
                    
                    <div className="button-container">
                        <button className="redirect-button" onClick={redirectToSahibinden}>
                            Sahibinden.com'da Piyasayı Gör
                        </button>
                    </div>
                </div>
            </div>
        </div>
    );
}

export default MarketValuePage;
