    description: ''
  });

  // 2. API ÇAĞRILARI
  // Araçları backend'den çekmek için bir fonksiyon
  const fetchVehicles = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/vehicles');
      setVehicles(response.data); // Gelen veriyi state'e ata
    } catch (error) {
      console.error("Araçlar çekilirken hata oluştu:", error);
    }
  };

  // Bu useEffect, bileşen ilk yüklendiğinde araçları otomatik olarak çeker
  useEffect(() => {
    fetchVehicles();
  }, []); // Boş dizi, bu fonksiyonun sadece bir kez çalışmasını sağlar

  // 3. EVENT HANDLER (OLAY YÖNETİCİLERİ)
  // Formdaki her input değiştiğinde çalışır ve formData state'ini günceller
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prevData => ({
      ...prevData,
      [name]: value
    }));
  };

  // Form gönderildiğinde (butona tıklandığında) çalışır
  const handleSubmit = async (e) => {
    e.preventDefault(); // Sayfanın yeniden yüklenmesini engelle
    try {
      // Form verilerini backend'e gönder
      await axios.post('http://localhost:5000/api/vehicles', formData);
      alert('Araç başarıyla eklendi!');
      
      // Formu temizle
      setFormData({
        brand: '', model: '', year: '', color: '', gear: '', fuel: '',
        mileage: '', purchase_price: '', sale_price: '', description: ''
      });
      
      // Yeni aracı da listede görmek için tabloyu güncelle
      fetchVehicles();

    } catch (error) {
      console.error("Araç eklenirken hata oluştu:", error);
      alert('Araç eklenirken bir hata oluştu.');
    }
  };

  return (
    <div className="vehicle-page-wrapper">
      <div className="vehicle-page">
        <div className="form-container">
          <h2 className="page-title">Araç Kayıt</h2>
          {/* Form'a handleSubmit fonksiyonunu bağlıyoruz */}
          <form className="vehicle-form" onSubmit={handleSubmit}>
            <div className="form-row">
              {/* Input'lara name, value ve onChange özelliklerini ekliyoruz */}
              <input type="text" name="brand" placeholder="Marka" value={formData.brand} onChange={handleInputChange} required />
              <input type="text" name="model" placeholder="Model" value={formData.model} onChange={handleInputChange} required />
              <input type="number" name="year" placeholder="Yıl" value={formData.year} onChange={handleInputChange} required />
            </div>
            <div className="form-row">
              <input type="text" name="color" placeholder="Renk" value={formData.color} onChange={handleInputChange} required />
              <input type="text" name="gear" placeholder="Vites" value={formData.gear} onChange={handleInputChange} required />
              <input type="text" name="fuel" placeholder="Yakıt" value={formData.fuel} onChange={handleInputChange} required />
            </div>
            <div className="form-row">
              <input type="number" name="mileage" placeholder="Kilometre" value={formData.mileage} onChange={handleInputChange} required />
              <input type="number" name="purchase_price" placeholder="Alış Fiyatı" value={formData.purchase_price} onChange={handleInputChange} required />
              <input type="number" name="sale_price" placeholder="Satış Fiyatı" value={formData.sale_price} onChange={handleInputChange} required />
            </div>
            <div className="form-row">
              <textarea name="description" placeholder="Açıklama" value={formData.description} onChange={handleInputChange}></textarea>
            </div>
            <button type="submit" className="add-vehicle-btn">Araç Ekle</button>
          </form>
        </div>

        <div className="card table-card">
          <h2>Kayıtlı Araçlar</h2>
          <div className="table-wrapper">
            {/* Tabloyu artık backend'den gelen "vehicles" state'i ile dolduruyoruz */}
            <table className="vehicle-table">
              <thead>
                <tr>
                  <th>Marka</th><th>Model</th><th>Yıl</th><th>Renk</th><th>Vites</th><th>Yakıt</th><th>KM</th><th>Alış</th><th>Satış</th><th>Açıklama</th><th>İşlemler</th>
                </tr>
              </thead>
              <tbody>
                {vehicles.map((vehicle) => (
                  <tr key={vehicle.id}>
                    <td>{vehicle.brand}</td>
                    <td>{vehicle.model}</td>
                    <td>{vehicle.year}</td>
                    <td>{vehicle.color}</td>
                    <td>{vehicle.gear}</td>
                    <td>{vehicle.fuel}</td>
                    <td>{vehicle.mileage.toLocaleString('tr-TR')}</td>
                    <td className="price-cell">{parseFloat(vehicle.purchase_price).toLocaleString('tr-TR', { style: 'currency', currency: 'TRY' })}</td>
                    <td className="price-cell">{parseFloat(vehicle.sale_price).toLocaleString('tr-TR', { style: 'currency', currency: 'TRY' })}</td>
                    <td>{vehicle.description}</td>
                    <td>
                      <div className="action-buttons">
                        <button className="edit-btn">Düzenle</button>
                        <button className="delete-btn">Sil</button>
                      </div>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      </div>
    </div>
  );
}

export default VehiclesPage;
