import React, { useState, useEffect, useRef } from 'react';
import io from 'socket.io-client';
import { jwtDecode } from 'jwt-decode';
import './ChatModal.css';

// Socket bağlantısını component dışında bir kere oluşturmak daha iyidir.
const socket = io('http://localhost:5000');

function ChatModal({ vehicle, closeModal }) {
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState('');
  const [currentUser, setCurrentUser] = useState(null);
  const messagesEndRef = useRef(null);

  // Component mount olduğunda kullanıcı bilgisini al
  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      try {
        const decoded = jwtDecode(token);
        setCurrentUser(decoded);
      } catch (e) {
        console.error("Geçersiz Token:", e);
        closeModal(); // Hatalı token varsa modal'ı kapat
      }
    }
  }, [closeModal]);

  // Sohbet odası kimliğini oluştur
  // Not: Admin ID'sini gelecekte dinamik hale getirebiliriz. Şimdilik 1 varsayıyoruz.
  const conversationId = currentUser ? `user_${currentUser.id}-admin_1-vehicle_${vehicle.id}` : null;

  useEffect(() => {
    // Sadece conversationId hazır olduğunda socket işlemlerini yap
    if (conversationId) {
      console.log(`[Sohbet Arayüzü] ${conversationId} odasına katılınıyor...`);
      socket.emit('join_room', conversationId);

      const handleLoadMessages = (loadedMessages) => {
        console.log(`[Sohbet Arayüzü] ${loadedMessages.length} adet geçmiş mesaj yüklendi.`);
        setMessages(loadedMessages);
      };

      const handleReceiveMessage = (message) => {
        console.log('[Sohbet Arayüzü] Yeni mesaj alındı:', message);
        setMessages((prevMessages) => [...prevMessages, message]);
      };

      socket.on('load_messages', handleLoadMessages);
      socket.on('receive_message', handleReceiveMessage);

      // Component unmount olduğunda (kapandığında) listener'ları temizle
      return () => {
        console.log(`[Sohbet Arayüzü] Listener'lar temizleniyor.`);
        socket.off('load_messages', handleLoadMessages);
        socket.off('receive_message', handleReceiveMessage);
      };
    }
  }, [conversationId]);

  // Her yeni mesaj geldiğinde en alta kaydır
  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages]);

  const handleSendMessage = (e) => {
    e.preventDefault();
    if (newMessage.trim() === '' || !currentUser || !conversationId) return;

    const messageData = {
      conversation_id: conversationId,
      sender_id: currentUser.id,
      receiver_id: 1, // Admin'in ID'si
      vehicle_id: vehicle.id,
      message: newMessage,
    };

    console.log('[Sohbet Arayüzü] Mesaj gönderiliyor:', messageData);
    socket.emit('send_message', messageData);
    setNewMessage('');
  };

  return (
    <div className="chat-modal-overlay" onClick={closeModal}>
      <div className="chat-modal-content" onClick={(e) => e.stopPropagation()}>
        <div className="chat-header">
          <h3>{vehicle.brand} {vehicle.model}</h3>
          <span>Satıcı ile Mesajlaşma</span>
          <button onClick={closeModal} className="close-chat-btn">×</button>
        </div>
        <div className="chat-messages">
          {messages.map((msg, index) => (
            <div
  key={msg.id}
  className={`message-bubble ${msg.sender_id === currentUser?.id ? 'sent' : 'received'}`}
>
              <p>{msg.message}</p>
              <span className="message-time">
                {new Date(msg.created_at).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}
              </span>
            </div>
          ))}
          <div ref={messagesEndRef} />
        </div>
        <form className="chat-input-form" onSubmit={handleSendMessage}>
          <input
            type="text"
            value={newMessage}
            onChange={(e) => setNewMessage(e.target.value)}
            placeholder="Mesajınızı yazın..."
            disabled={!currentUser} // Kullanıcı bilgisi yüklenene kadar input'u kilitle
          />
          <button type="submit" disabled={!currentUser}>Gönder</button>
        </form>
      </div>
    </div>
  );
}

export default ChatModal;
