// src/AdminChatBox.js
import React, { useState, useEffect, useRef } from 'react';
import io from 'socket.io-client';
import { jwtDecode } from 'jwt-decode';
import './AdminChatBox.css';

const socket = io('http://localhost:5000');

function AdminChatBox({ conversationId }) {
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState('');
  const [adminUser, setAdminUser] = useState(null);
  const [conversationInfo, setConversationInfo] = useState({});
  const messagesEndRef = useRef(null);

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) { const decoded = jwtDecode(token); setAdminUser(decoded); }
  }, []);

  useEffect(() => {
    if (conversationId) {
      const parts = conversationId.split('-');
      const userId = parts[0].split('_')[1];
      const vehicleId = parts[2].split('_')[1];
      setConversationInfo({ userId, vehicleId });
      socket.emit('join_room', conversationId);
      socket.on('load_messages', setMessages);
      socket.on('receive_message', (message) => setMessages((prev) => [...prev, message]));
      return () => { socket.off('load_messages'); socket.off('receive_message'); };
    }
  }, [conversationId]);

  useEffect(() => { messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' }); }, [messages]);

  const handleSendMessage = (e) => {
    e.preventDefault();
    if (newMessage.trim() === '' || !adminUser || !conversationInfo.userId) return;
    const messageData = {
      conversation_id: conversationId,
      sender_id: adminUser.id,
      receiver_id: parseInt(conversationInfo.userId),
      vehicle_id: parseInt(conversationInfo.vehicleId),
      message: newMessage,
    };
    socket.emit('send_message', messageData);
    setNewMessage('');
  };

  if (!conversationId) return null;

  return (
    <div className="admin-chat-box">
      <div className="chat-messages-admin">
        {messages.map((msg, index) => (
          <div
            key={msg.id || index}
            // === KONTROL BURADA ===
            className={`message-bubble-admin ${msg.sender_id === adminUser?.id ? 'sent-admin' : 'received-admin'}`}
          >
            <p>{msg.message}</p>
            <span className="message-time-admin">{new Date(msg.created_at).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}</span>
          </div>
        ))}
        <div ref={messagesEndRef} />
      </div>
      <form className="chat-input-form-admin" onSubmit={handleSendMessage}>
        <input type="text" value={newMessage} onChange={(e) => setNewMessage(e.target.value)} placeholder="Cevabınızı yazın..."/>
        <button type="submit">➢</button>
      </form>
    </div>
  );
}
export default AdminChatBox;
