// export default LoginPage;
import React, { useState } from 'react';
import axios from 'axios';
import './LoginPage.css';
import { useNavigate } from 'react-router-dom';
import { jwtDecode } from 'jwt-decode';

function LoginPage() {
  const [activeTab, setActiveTab] = useState('admin'); // 'admin', 'user', 'register'

  const [adminEmail, setAdminEmail] = useState('');
  const [adminPassword, setAdminPassword] = useState('');

  const [userEmail, setUserEmail] = useState('');
  const [userPassword, setUserPassword] = useState('');

  const [registerName, setRegisterName] = useState('');
  const [registerEmail, setRegisterEmail] = useState('');
  const [registerPassword, setRegisterPassword] = useState('');

  const [error, setError] = useState('');
  const navigate = useNavigate();

  const clearErrors = () => setError('');

  const handleAdminLogin = async (e) => {
    e.preventDefault();
    setError('');
    try {
      const res = await axios.post('http://localhost:5000/api/login', {
        email: adminEmail,
        password: adminPassword
      });
      const token = res.data.token;
      const decoded = jwtDecode(token);

      if (decoded.role !== 'admin') {
        setError('Bu alan sadece yöneticilere aittir.');
        return;
      }

      localStorage.setItem('token', token);
      navigate('/dashboard');
    } catch (err) {
      setError(err.response?.data?.message || 'Giriş işlemi başarısız.');
    }
  };

  const handleUserLogin = async (e) => {
    e.preventDefault();
    setError('');
    try {
      const res = await axios.post('http://localhost:5000/api/login', {
        email: userEmail,
        password: userPassword
      });
      const token = res.data.token;
      localStorage.setItem('token', token);
      navigate('/home');
    } catch (err) {
      setError(err.response?.data?.message || 'Giriş işlemi başarısız.');
    }
  };

  const handleRegister = async (e) => {
    e.preventDefault();
    setError('');
    try {
  await axios.post('http://localhost:5000/api/register', {
    name: registerName,
    email: registerEmail,
    password: registerPassword
  });
  alert('Kayıt başarılı! Şimdi giriş yapabilirsiniz.');
  setActiveTab('user');
} catch (err) {
  setError(err.response?.data?.message || 'Kayıt başarısız.');
}

  };

  return (
    <div className="login-container">
      <div className="login-wrapper">
        <div className="role-tabs">
          <button
            className={`tab-button ${activeTab === 'admin' ? 'active' : ''}`}
            onClick={() => { setActiveTab('admin'); clearErrors(); }}
          >
            Yönetici Girişi
          </button>
          <button
            className={`tab-button ${activeTab === 'user' ? 'active' : ''}`}
            onClick={() => { setActiveTab('user'); clearErrors(); }}
          >
            Kullanıcı Girişi
          </button>
          <button
            className={`tab-button ${activeTab === 'register' ? 'active' : ''}`}
            onClick={() => { setActiveTab('register'); clearErrors(); }}
          >
            Kayıt Ol
          </button>
        </div>

        {/* === Yönetici Girişi === */}
        {activeTab === 'admin' && (
          <form className="login-form" onSubmit={handleAdminLogin}>
            <h2>Yönetici Girişi</h2>
            <input
              type="email"
              placeholder="E-posta"
              value={adminEmail}
              onChange={(e) => setAdminEmail(e.target.value)}
              required
            />
            <input
              type="password"
              placeholder="Şifre"
              value={adminPassword}
              onChange={(e) => setAdminPassword(e.target.value)}
              required
            />
            <button type="submit">Giriş Yap</button>
            {error && <p className="error">{error}</p>}
            <div className="form-switch">
              <span onClick={() => navigate('/forgot-password')}>Şifremi Unuttum</span>
            </div>
          </form>
        )}

        {/* === Kullanıcı Girişi === */}
        {activeTab === 'user' && (
          <form className="login-form" onSubmit={handleUserLogin}>
            <h2>Kullanıcı Girişi</h2>
            <input
              type="email"
              placeholder="E-posta"
              value={userEmail}
              onChange={(e) => setUserEmail(e.target.value)}
              required
            />
            <input
              type="password"
              placeholder="Şifre"
              value={userPassword}
              onChange={(e) => setUserPassword(e.target.value)}
              required
            />
            <button type="submit">Giriş Yap</button>
            {error && <p className="error">{error}</p>}
            <div className="form-switch">
              <span onClick={() => setActiveTab('register')}>Kaydol</span>
              <span onClick={() => navigate('/forgot-password')}>Şifremi Unuttum</span>
            </div>
          </form>
        )}

        {/* === Kayıt Formu === */}
        {activeTab === 'register' && (
          <form className="login-form" onSubmit={handleRegister}>
            <h2>Kayıt Ol</h2>
            <input
              type="text"
              placeholder="Ad Soyad"
              value={registerName}
              onChange={(e) => setRegisterName(e.target.value)}
              required
            />
            <input
              type="email"
              placeholder="E-posta"
              value={registerEmail}
              onChange={(e) => setRegisterEmail(e.target.value)}
              required
            />
            <input
              type="password"
              placeholder="Şifre"
              value={registerPassword}
              onChange={(e) => setRegisterPassword(e.target.value)}
              required
            />
            <button type="submit">Kaydol</button>
            {error && <p className="error">{error}</p>}
          </form>
        )}
      </div>
    </div>
  );
}

export default LoginPage;
