import React, { useState, useEffect, useRef } from 'react';
import io from 'socket.io-client';
import { jwtDecode } from 'jwt-decode';
import './AdminChatBox.css';

const socket = io('http://localhost:5000');

function AdminChatBox({ conversationId, vehicleInfo, userInfo }) {
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState('');
  const [adminUser, setAdminUser] = useState(null);
  const messagesEndRef = useRef(null);

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      try {
        const decoded = jwtDecode(token);
        setAdminUser(decoded);
        console.log('Admin kullanƒ±cƒ±sƒ± y√ºklendi:', decoded);
      } catch (error) {
        console.error('Token decode hatasƒ±:', error);
      }
    }
  }, []);

  useEffect(() => {
    if (conversationId && adminUser) {
      console.log('=== ADMIN CHAT BOX ===');
      console.log('Conversation ID:', conversationId);
      console.log('Admin User ID:', adminUser.id);
      console.log('User Info:', userInfo);
      console.log('Vehicle Info:', vehicleInfo);

      // √ñnceki baƒülantƒ±larƒ± temizle
      socket.off('load_messages');
      socket.off('receive_message');
      socket.off('error');
      
      // Yeni odaya katƒ±l
      socket.emit('join_room', {
        conversationId: conversationId,
        userId: adminUser.id
      });
      
      // Mesajlarƒ± temizle
      setMessages([]);

      const handleLoadMessages = (loadedMessages) => {
        console.log('Admin - Y√ºklenen mesajlar:', loadedMessages);
        // Her mesaj i√ßin sender bilgisini kontrol et
        const processedMessages = loadedMessages.map(msg => {
          console.log(`Mesaj ID: ${msg.id}, Sender ID: ${msg.sender_id}, Admin ID: ${adminUser.id}`);
          return {
            ...msg,
            isFromAdmin: parseInt(msg.sender_id) === parseInt(adminUser.id)
          };
        });
        setMessages(processedMessages);
      };

      const handleReceiveMessage = (message) => {
        console.log('Admin - Yeni mesaj alƒ±ndƒ±:', message);
        console.log(`Yeni mesaj - Sender ID: ${message.sender_id}, Admin ID: ${adminUser.id}`);
        
        const processedMessage = {
          ...message,
          isFromAdmin: parseInt(message.sender_id) === parseInt(adminUser.id)
        };
        
        setMessages((prev) => [...prev, processedMessage]);
      };

      const handleError = (error) => {
        console.error('Socket hatasƒ±:', error);
        alert('Baƒülantƒ± hatasƒ±: ' + error.message);
      };

      // Event listener'larƒ± ekle
      socket.on('load_messages', handleLoadMessages);
      socket.on('receive_message', handleReceiveMessage);
      socket.on('error', handleError);

      return () => {
        socket.off('load_messages', handleLoadMessages);
        socket.off('receive_message', handleReceiveMessage);
        socket.off('error', handleError);
      };
    }
  }, [conversationId, adminUser, userInfo, vehicleInfo]);

  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages]);

  const handleSendMessage = (e) => {
    e.preventDefault();
    
    if (newMessage.trim() === '') {
      console.log('Bo≈ü mesaj g√∂nderilemez');
      return;
    }
    
    if (!adminUser) {
      console.log('Admin kullanƒ±cƒ±sƒ± bulunamadƒ±');
      alert('Oturum hatasƒ±! L√ºtfen yeniden giri≈ü yapƒ±n.');
      return;
    }
    
    if (!userInfo) {
      console.log('Kullanƒ±cƒ± bilgisi bulunamadƒ±');
      alert('Kullanƒ±cƒ± bilgisi bulunamadƒ±!');
      return;
    }

    // Conversation ID'den veya userInfo'dan receiver ID'yi bul
    let receiverId = null;
    
    if (conversationId.includes('user_')) {
      const match = conversationId.match(/user_(\d+)/);
      if (match) {
        receiverId = parseInt(match[1]);
      }
    }
    
    // Yedek olarak userInfo'dan al
    if (!receiverId) {
      receiverId = userInfo.user_id || userInfo.sender_id || userInfo.id;
    }

    if (!receiverId) {
      console.error('Alƒ±cƒ± ID bulunamadƒ±!');
      console.error('UserInfo:', userInfo);
      console.error('ConversationId:', conversationId);
      alert('Alƒ±cƒ± bilgisi bulunamadƒ±!');
      return;
    }
    
    const messageData = {
      conversation_id: conversationId,
      sender_id: parseInt(adminUser.id), // Admin g√∂nderdiƒüi i√ßin admin ID
      receiver_id: parseInt(receiverId), // Kullanƒ±cƒ±nƒ±n ID'si
      vehicle_id: vehicleInfo?.vehicle_id || vehicleInfo?.id || null,
      message: newMessage.trim(),
    };

    console.log('=== ADMIN MESAJ G√ñNDERƒ∞YOR ===');
    console.log('Mesaj verisi:', JSON.stringify(messageData, null, 2));
    
    // Socket baƒülantƒ± kontrol√º
    if (!socket.connected) {
      console.error('Socket baƒülantƒ±sƒ± yok!');
      alert('Baƒülantƒ± hatasƒ±! Sayfa yenilenecek.');
      window.location.reload();
      return;
    }

    socket.emit('send_message', messageData);
    setNewMessage('');
  };

  if (!conversationId) {
    return (
      <div className="admin-chat-box-empty">
        <div className="empty-state">
          <h3>üí¨ Sohbet Se√ßin</h3>
          <p>Mesajla≈ümaya ba≈ülamak i√ßin soldaki listeden bir konu≈üma se√ßin.</p>
        </div>
      </div>
    );
  }

  return (
    <div className="admin-chat-box">
      <div className="chat-header-admin">
        <div className="chat-info">
          <h3>üë§ {userInfo?.user_name || userInfo?.sender_name || 'Kullanƒ±cƒ±'}</h3>
          <span>üöó {vehicleInfo?.brand} {vehicleInfo?.model} {vehicleInfo?.year}</span>
          <small style={{fontSize: '11px', color: '#666'}}>
            ID: {conversationId}
          </small>
        </div>
        <div className="online-status">
          <span className="online-dot"></span>
          <span>Aktif</span>
        </div>
      </div>
      
      <div className="chat-messages-admin">
        {messages.length === 0 ? (
          <div className="no-messages">
            <p>üí¨ Hen√ºz mesaj yok</p>
            <p>ƒ∞lk mesajƒ± g√∂ndererek sohbeti ba≈ülatƒ±n!</p>
          </div>
        ) : (
          messages.map((msg, index) => {
            // Mesajƒ±n admin'den mi geldiƒüini kontrol et
            const isFromCurrentAdmin = parseInt(msg.sender_id) === parseInt(adminUser.id);
            
            console.log(`Mesaj ${index + 1}:`, {
              id: msg.id,
              sender_id: msg.sender_id,
              admin_id: adminUser.id,
              isFromCurrentAdmin,
              message: msg.message
            });

            return (
              <div
                key={msg.id || `msg-${index}`}
                className={`message-bubble-admin ${isFromCurrentAdmin ? 'sent-admin' : 'received-admin'}`}
              >
                <div className="message-content">
                  <p>{msg.message}</p>
                </div>
                <div className="message-info">
                  <small className="message-sender">
                    {isFromCurrentAdmin ? 'üë®‚Äçüíº Admin' : `üë§ ${msg.sender_name || 'Kullanƒ±cƒ±'}`}
                  </small>
                  <span className="message-time-admin">
                    {new Date(msg.created_at).toLocaleTimeString('tr-TR', { 
                      hour: '2-digit', 
                      minute: '2-digit' 
                    })}
                  </span>
                </div>
              </div>
            );
          })
        )}
        <div ref={messagesEndRef} />
      </div>
      
      <form className="chat-input-form-admin" onSubmit={handleSendMessage}>
        <div className="input-container">
          <input
            type="text"
            value={newMessage}
            onChange={(e) => setNewMessage(e.target.value)}
            placeholder="Cevabƒ±nƒ±zƒ± yazƒ±n..."
            disabled={!adminUser}
            maxLength={1000}
          />
          <button 
            type="submit" 
            disabled={!adminUser || !newMessage.trim()}
            className="send-button"
          >
            üì§
          </button>
        </div>
      </form>
      
      {/* Debug Paneli - sadece development'da g√∂r√ºn√ºr */}
      {process.env.NODE_ENV === 'development' && (
        <div className="debug-panel" style={{ 
          fontSize: '10px', 
          background: '#f0f0f0', 
          padding: '8px', 
          margin: '8px',
          borderRadius: '4px',
          color: '#666'
        }}>
          <div><strong>üîß Debug Bilgileri:</strong></div>
          <div>Admin ID: {adminUser?.id}</div>
          <div>Admin Adƒ±: {adminUser?.name}</div>
          <div>Kullanƒ±cƒ± ID: {userInfo?.user_id || userInfo?.sender_id || 'N/A'}</div>
          <div>Kullanƒ±cƒ± Adƒ±: {userInfo?.user_name || userInfo?.sender_name || 'N/A'}</div>
          <div>Ara√ß ID: {vehicleInfo?.vehicle_id || vehicleInfo?.id || 'N/A'}</div>
          <div>Socket Baƒülƒ±: {socket.connected ? '‚úÖ' : '‚ùå'}</div>
          <div>Mesaj Sayƒ±sƒ±: {messages.length}</div>
        </div>
      )}
    </div>
  );
}

export default AdminChatBox;
