// src/AdminChatBox.js
import React, { useState, useEffect, useRef } from 'react';
import io from 'socket.io-client';
import { jwtDecode } from 'jwt-decode';
import './AdminChatBox.css';

const socket = io('http://localhost:5000');

function AdminChatBox({ conversation }) {
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState('');
  const [adminUser, setAdminUser] = useState(null);
  const messagesEndRef = useRef(null);

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) setAdminUser(jwtDecode(token));
  }, []);

  useEffect(() => {
    socket.emit('join_room', conversation.conversation_id);
    socket.on('load_messages', setMessages);
    socket.on('receive_message', (message) => {
      if(message.conversation_id === conversation.conversation_id) {
        setMessages((prev) => [...prev, message]);
      }
    });
    return () => { socket.off('load_messages'); socket.off('receive_message'); };
  }, [conversation.conversation_id]);

  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages]);

  const handleSendMessage = (e) => {
    e.preventDefault();
    if (newMessage.trim() === '' || !adminUser) return;
    const messageData = {
      conversation_id: conversation.conversation_id,
      sender_id: adminUser.id,
      receiver_id: conversation.user_id,
      vehicle_id: conversation.vehicle_id,
      message: newMessage,
    };
    socket.emit('send_message', messageData);
    setNewMessage('');
  };

  return (
    <div className="admin-chat-box">
      <div className="chat-header">
          <h3>{conversation.user_name}</h3>
          <span>{conversation.brand} {conversation.model}</span>
      </div>
      <div className="chat-messages-admin">
        {messages.map((msg) => (
          <div key={msg.id} className={`message-bubble-admin ${msg.sender_id === adminUser?.id ? 'sent-admin' : 'received-admin'}`}>
            <p>{msg.message}</p>
            <span className="message-time-admin">{new Date(msg.created_at).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}</span>
          </div>
        ))}
        <div ref={messagesEndRef} />
      </div>
      <form className="chat-input-form-admin" onSubmit={handleSendMessage}>
        <input type="text" value={newMessage} onChange={(e) => setNewMessage(e.target.value)} placeholder="Cevabınızı yazın..." />
        <button type="submit">➢</button>
      </form>
    </div>
  );
}
export default AdminChatBox;
