import React from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import './App.css';

// KORUYUCULAR
import ProtectedRoute from './ProtectedRoute'; 
import AdminProtectedRoute from './AdminProtectedRoute'; 

// SAYFALAR
import LoginPage from './LoginPage';
import ForgotPasswordPage from './ForgotPasswordPage';
import UserDashboard from './UserDashboard'; // Kullanıcının yeni ana sayfası
import Dashboard from './Dashboard';        // YÖNETİCİNİN ana sayfası
import VehiclesPage from './VehiclesPage';  // Yöneticinin araç yönetim sayfası
import PersonnelPage from './PersonnelPage'; // Yöneticinin personel sayfası
//... ve diğerleri

function App() {
  return (
    <Router>
      <Routes>
        {/* Herkesin Erişebileceği Rotalar */}
        <Route path="/" element={<LoginPage />} />
        <Route path="/forgot-password" element={<ForgotPasswordPage />} />

        {/* ======================================= */}
        {/*      KULLANICIYA ÖZEL ROTA            */}
        {/* ======================================= */}
        <Route 
          path="/user-dashboard" 
          element={
            <ProtectedRoute>
              <UserDashboard />
            </ProtectedRoute>
          } 
        />

        {/* ======================================= */}
        {/*      YÖNETİCİYE ÖZEL ROTALAR            */}
        {/* ======================================= */}
        <Route 
          path="/dashboard" 
          element={
            <AdminProtectedRoute>
              <Dashboard />
            </AdminProtectedRoute>
          } 
        />
        <Route 
          path="/admin/vehicles" 
          element={
            <AdminProtectedRoute>
              <VehiclesPage />
            </AdminProtectedRoute>
          } 
        />
        <Route 
          path="/admin/personnel" 
          element={
            <AdminProtectedRoute>
              <PersonnelPage />
            </AdminProtectedRoute>
          } 
        />
        {/* Diğer admin rotaları da buraya... */}

      </Routes>
    </Router>
  );
}

export default App;
