import React, { useState, useEffect, useMemo } from 'react';
import axios from 'axios';
import './VehiclesPage.css';

function VehiclesPage() {
  const [vehicles, setVehicles] = useState([]);
  const [isEditing, setIsEditing] = useState(false);
  const [currentVehicle, setCurrentVehicle] = useState(null); // Artık tüm aracı tutuyoruz
  const [filter, setFilter] = useState('');
  
  // ===> DEĞİŞİKLİK 1: Fotoğraf state'i artık bir dizi (array) <===
  const [photoFiles, setPhotoFiles] = useState([]);

  const initialFormData = {
    brand: '', model: '', year: '', color: '', gear: '', fuel: '',
    mileage: '', purchase_price: '', sale_price: '', description: ''
  };
  const [formData, setFormData] = useState(initialFormData);

  const fetchVehicles = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/vehicles');
      setVehicles(response.data);
    } catch (error) { console.error("Araçlar çekilirken hata oluştu:", error); }
  };

  useEffect(() => { fetchVehicles(); }, []);

  const handleInputChange = (e) => {
    setFormData(prev => ({ ...prev, [e.target.name]: e.target.value }));
  };

  // ===> DEĞİŞİKLİK 2: Çoklu dosya seçimi için handle fonksiyonu <===
  const handleFileChange = (e) => {
    setPhotoFiles(Array.from(e.target.files)); // Seçilen tüm dosyaları bir diziye çevir
  };

  const cancelEdit = () => {
    setIsEditing(false);
    setCurrentVehicle(null);
    setFormData(initialFormData);
    setPhotoFiles([]);
  };

  // ===> DEĞİŞİKLİK 3: handleEdit artık aracın fotoğraflarını da çekiyor <===
  const handleEdit = async (vehicle) => {
    try {
        // Backend'den aracın tüm detaylarını (fotoğraflar dahil) çekiyoruz
        const response = await axios.get(`http://localhost:5000/api/vehicles/${vehicle.id}`);
        setIsEditing(true);
        setCurrentVehicle(response.data); // Gelen tüm veriyi state'e at
        setFormData(response.data); // Formu bu veriyle doldur
        setPhotoFiles([]);
        window.scrollTo(0, 0);
    } catch (error) {
        alert("Araç detayları çekilirken bir hata oluştu.");
    }
  };
  
  // ===> YENİ FONKSİYON: Tek bir fotoğrafı silmek için <===
  const handleDeletePhoto = async (photoId) => {
    if (window.confirm("Bu fotoğrafı kalıcı olarak silmek istediğinizden emin misiniz?")) {
        try {
            const token = localStorage.getItem('token');
            await axios.delete(`http://localhost:5000/api/photos/${photoId}`, {
                headers: { 'Authorization': `Bearer ${token}` }
            });
            alert("Fotoğraf silindi.");
            // State'i güncelleyerek silinen fotoğrafı anında arayüzden kaldır
            setCurrentVehicle(prev => ({
                ...prev,
                photos: prev.photos.filter(p => p.id !== photoId)
            }));
        } catch (error) {
            alert("Fotoğraf silinirken bir hata oluştu.");
        }
    }
  };

  const handleDelete = async (id) => { /* ... (Bu fonksiyonda değişiklik yok) ... */ };

  // ===> DEĞİŞİKLİK 4: handleSubmit artık çoklu dosya gönderiyor <===
  const handleSubmit = async (e) => {
    e.preventDefault();
    const vehicleData = new FormData();
    for (const key in formData) { vehicleData.append(key, formData[key]); }
    
    // Seçilen tüm dosyaları FormData'ya ekle
    photoFiles.forEach(file => {
      vehicleData.append('photos', file); // Backend'deki .array('photos') ile aynı isim olmalı
    });

    const token = localStorage.getItem('token');
    const config = { headers: { 'Content-Type': 'multipart/form-data', 'Authorization': `Bearer ${token}` } };
    
    // Not: Düzenleme endpoint'i artık PUT değil, POST olacak çünkü dosya da gönderiyoruz
    // ve PUT ile dosya göndermek karmaşık olabilir. Backend'de bunu yöneteceğiz.
    const url = isEditing ? `http://localhost:5000/api/vehicles/${currentVehicle.id}/add-photos` : 'http://localhost:5000/api/vehicles';
    // Şimdilik sadece yeni ekleme fonksiyonunu güncelliyoruz.
    const method = 'post'; 
    
    try {
      await axios[method](url, vehicleData, config);
      alert(isEditing ? 'Fotoğraflar başarıyla eklendi!' : 'Araç başarıyla eklendi!');
      cancelEdit();
      fetchVehicles();
    } catch (error) {
      alert(isEditing ? 'İşlem sırasında bir hata oluştu.' : 'Araç eklenirken bir hata oluştu.');
    }
  };

  const filteredVehicles = useMemo(() => { /* ... (Bu fonksiyonda değişiklik yok) ... */ }, [vehicles, filter]);

  return (
    <div className="vehicle-page-wrapper">
      <div className="vehicle-page">
        <div className="form-container">
          <h2 className="page-title">{isEditing ? 'Aracı Düzenle' : 'Yeni Araç Kayıt'}</h2>
          <form className="vehicle-form" onSubmit={handleSubmit}>
            {/* ... (Diğer inputlarınız aynı kalıyor) ... */}
            <div className="form-row">
              <textarea name="description" placeholder="Açıklama" value={formData.description} onChange={handleInputChange}></textarea>
            </div>
            
            <div className="form-row">
              <div>
                <label htmlFor="photo-upload" className="photo-upload-label">
                  {isEditing ? 'Yeni Fotoğraflar Ekle' : 'Araç Fotoğrafları (En fazla 10 adet)'}
                </label>
                <input 
                  id="photo-upload"
                  type="file" 
                  name="photos" 
                  onChange={handleFileChange} 
                  multiple // ===> Çoklu seçime izin ver <===
                  accept="image/*" // Sadece resim dosyalarını göster
                />
              </div>
            </div>
            <div className="form-buttons-container">
              <button type="submit" className="add-vehicle-btn">
                {isEditing ? 'Değişiklikleri Kaydet' : 'Aracı Ekle'}
              </button>
              {isEditing && <button type="button" className="add-vehicle-btn cancel-edit-btn" onClick={cancelEdit}>İptal</button>}
            </div>
          </form>

          {/* ===> YENİ: DÜZENLEME MODUNDA FOTOĞRAF YÖNETİMİ <=== */}
          {isEditing && currentVehicle && currentVehicle.photos.length > 0 && (
            <div className="photo-management-container">
                <h3>Mevcut Fotoğraflar</h3>
                <div className="photo-grid">
                    {currentVehicle.photos.map(photo => (
                        <div key={photo.id} className="photo-item">
                            <img src={`http://localhost:5000/${photo.photo_url}`} alt="Araç fotoğrafı"/>
                            <button onClick={() => handleDeletePhoto(photo.id)} className="delete-photo-btn">Sil</button>
                        </div>
                    ))}
                </div>
            </div>
          )}
        </div>
        
        <div className="table-card">
          {/* ... (Tablo yapınız aynı kalıyor, sadece ana fotoğrafı gösterecek şekilde güncellenebilir) ... */}
        </div>
      </div>
    </div>
  );
}
export default VehiclesPage;
